let prev=document.querySelector(".slider__control--prev"),next=document.querySelector(".slider__control--next"),slidesFront=document.querySelectorAll(".slider__front-item"),slidesBack=document.querySelectorAll(".slider__back-item"),slideIndex=1;function showSlides(e,s){e>s.length&&(slideIndex=1),e<1&&(slideIndex=s.length);for(let e of s)e.style.display="none";s[slideIndex-1].style.display="block"}function showSlidesBack(){showSlides(slideIndex,slidesBack)}showSlides(slideIndex,slidesFront),showSlides(slideIndex,slidesBack),prev.onclick=function(){showSlides(slideIndex-=1,slidesFront),setTimeout(showSlidesBack,500)},next.onclick=function(){showSlides(slideIndex+=1,slidesFront),setTimeout(showSlidesBack,500)};